mapper.update:
update exam.student
set s_account = #{sAccount,jdbcType=VARCHAR},
  s_name = #{sName,jdbcType=VARCHAR},
  sc_id = #{scId,jdbcType=VARCHAR}
where s_id = #{sId,jdbcType=INTEGER}

mapper.select:
select s_id, s_account, s_name, sc_id
from exam.student
where s_id = #{sId,jdbcType=INTEGER}

select s_id, s_account, s_name, sc_id
from exam.student
where s_account = #{account, jdbcType=VARCHAR}

select *
from exam.student
where sc_id = #{scId, jdbcType=VARCHAR}
